from mcstatus.forge_data import ForgeData, decode_forge_data, decode_optimized

ENCODED_STRING = bytes.fromhex(
    ""
).decode("utf-8")


def test_forge_data_build() -> None:
    assert ForgeData.build({"fmlNetworkVersion": 3, "channels": [], "mods": []}) == ForgeData(
        fml_network_version=3, channels=[], mods=[], truncated=False
    )


def test_decode_optimized() -> None:
    buffer = decode_optimized(ENCODED_STRING)
    assert buffer.received == bytearray.fromhex(
        ""
    )


